CREATE TABLE towns (
  id           INT(6) PRIMARY KEY NOT NULL,
  city         VARCHAR(32),
  ru_city      VARCHAR(32),
  coord_x      INT(6),
  coord_y      INT(6),
  coord_x_name INT(6),
  coord_y_name INT(6)
);



CREATE TABLE boiler (
  id      INT(6) PRIMARY KEY NOT NULL,
  name    VARCHAR(64),
  town    INT(6),
  address VARCHAR(64),
  tel     VARCHAR(16),
  CONSTRAINT boiler_towns_fk FOREIGN KEY (town) REFERENCES towns (id)
);


CREATE TABLE control_objects (
  id                INT(10) PRIMARY KEY NOT NULL,
  name              VARCHAR(64),
  address           VARCHAR(64),
  town              VARCHAR(64),
  tel               VARCHAR(64),
  dev_model         VARCHAR(64),
  startdt           DATETIME,
  stopdt            DATETIME,
  isincludedinquery TINYINT,
  isqueryfinished   TINYINT,
  archive_num       VARCHAR(16),
  incr_step         INT(4)
);


CREATE TABLE sensors (
  id_co     INT(10) PRIMARY KEY,
  boiler_id INT(6),
  CONSTRAINT sensors_boiler_fk FOREIGN KEY (boiler_id) REFERENCES boiler (id),
  CONSTRAINT sensors_co_fk FOREIGN KEY (id_co) REFERENCES control_objects (id)
);


CREATE TABLE param_descr (
  id         INT(6) PRIMARY KEY NOT NULL,
  chanel_num VARCHAR(16),
  par_num    INT(4),
  par_name   VARCHAR(16),
  par_dim    VARCHAR(8),
  par_descr  VARCHAR(255),
  dev_type   VARCHAR(16)
);


CREATE TABLE actual_param_values (
  id_pd     INT(6) NOT NULL DEFAULT '1',
  par_value VARCHAR(16) NOT NULL DEFAULT '0',
  date_time DATETIME DEFAULT '0001-01-01 01:01:01',
  id_co     INT(10) NOT NULL DEFAULT '1',
  par_name  VARCHAR(16) DEFAULT NULL,
  par_dim   VARCHAR(8) NOT NULL DEFAULT '-',
  par_num   INT(4) NOT NULL DEFAULT '1',
  CONSTRAINT apv_co_fk FOREIGN KEY (id_co) REFERENCES control_objects (id),
  CONSTRAINT apv_pd_fk FOREIGN KEY (id_pd) REFERENCES param_descr (id)
);


